// ===========================================================================//	Crystal.h// ===========================================================================#pragma once#include "Point3DFloat.h"class Crystal {	public:						Crystal();						Crystal(Crystal &inXl);		Crystal		operator= (Crystal &inXl);		Boolean		operator== (Crystal &inXl);		inline Boolean	operator!= (Crystal &inXl) {return !(inXl == *this);};		inline Boolean		PointInside (Point3DFloat &inPt) {return (ctr.Distance(inPt) < r);};	public:		// I'm making these public, in violation of good style,					// in order to keep function calls to a minimum		Point3DFloat	ctr;		/* the coordinates of the center of the sphere */		float r;			/* The sphere's radius */		float actV;		float extV;		float ctcDist;		float polyV;		float diffV;		short neighbor;		short ctrSlice;	/* The slice in which the center of the sphere lies */		long ctrID;		/* The ID of the circle in which the center of the sphere lies */};